#include <bits/stdc++.h>using namespace std; #define endl '\n'#define int long long#define yes cout << "YES" << endl#define no cout << "NO" << endl signed main(){    ios::sync_with_stdio(false);    cin.tie(NULL);     string s;    cin >> s;    int n = s.size();     for (int i = 0; i < n; i++)    {        int a = s[i] - '0';        if (a % 8 == 0)        {            yes;            cout << a << endl;            return 0;        }        for (int j = i + 1; j < n; j++)        {            int b = a * 10 + (s[j] - '0');            if (b % 8 == 0)            {                yes;                cout << b << endl;                return 0;            }            for (int k = j + 1; k < n; k++)            {                int c = b * 10 + (s[k] - '0');                if (c % 8 == 0)                {                    yes;                    cout << c << endl;                    return 0;                }            }        }    }     no;    return 0;}